// src/lib/supabase-image-utils.ts
// Utilidades para manejar imágenes de Supabase

/**
 * Verifica si una URL es de Supabase Storage
 */
export const isSupabaseUrl = (url: string): boolean => {
  return url.includes('supabase.co') && url.includes('/storage/v1/object/public/');
};

/**
 * Verifica la accesibilidad de una imagen de Supabase
 */
export const checkImageAccessibility = async (url: string): Promise<boolean> => {
  if (!isSupabaseUrl(url)) {
    return true; // No es una URL de Supabase, asumir que es accesible
  }

  try {
    const response = await fetch(url, { 
      method: 'HEAD',
      mode: 'no-cors' // Evitar problemas de CORS
    });
    
    // En modo no-cors, no podemos verificar el status, pero si no hay error, asumir que es accesible
    return true;
  } catch (error) {
    console.warn('⚠️ No se pudo verificar la accesibilidad de la imagen:', url, error);
    return false;
  }
};

/**
 * Genera una URL de imagen de Supabase con parámetros de optimización
 */
export const getOptimizedSupabaseImageUrl = (
  url: string, 
  width?: number, 
  height?: number, 
  quality: number = 75
): string => {
  if (!isSupabaseUrl(url)) {
    return url;
  }

  // Para Supabase, no podemos optimizar la URL directamente
  // Pero podemos agregar parámetros de consulta si es necesario
  const urlObj = new URL(url);
  
  if (width) {
    urlObj.searchParams.set('width', width.toString());
  }
  
  if (height) {
    urlObj.searchParams.set('height', height.toString());
  }
  
  urlObj.searchParams.set('quality', quality.toString());
  
  return urlObj.toString();
};

/**
 * Obtiene el nombre del bucket de una URL de Supabase
 */
export const getSupabaseBucketFromUrl = (url: string): string | null => {
  if (!isSupabaseUrl(url)) {
    return null;
  }

  const match = url.match(/\/storage\/v1\/object\/public\/([^\/]+)\//);
  return match ? match[1] : null;
};

/**
 * Genera una URL de fallback para imágenes de Supabase
 */
export const getSupabaseImageFallback = (bucket: string): string => {
  const fallbacks: Record<string, string> = {
    hero: 'data:image/svg+xml;base64,',
    about: 'data:image/svg+xml;base64,',
    courses: 'data:image/svg+xml;base64,',
    services: 'data:image/svg+xml;base64,',
    products: 'data:image/svg+xml;base64,',
    perfumes: 'data:image/svg+xml;base64,',
    gallery: 'data:image/svg+xml;base64,'
  };

  return fallbacks[bucket] || fallbacks.hero;
};





